/*
Code snippet of scrolling animation
*/

(function (e) {
	'use strict';
	// class == scroll-animation
	e('a.scroll-animation[href*="#"]:not([href="#"])').click(function () {
		if (
			location.pathname.replace(/^\//, '') == this.pathname.replace(/^\//, '') &&
			location.hostname == this.hostname
		) {
			//figure out element to scroll to
			var t = e(this.hash);
			if ((t = t.length ? t : e('[name=' + this.hash.slice(1) + ']')).length)
				return (
					e('html, body').animate(
						{ scrollTop: t.offset().top - 60 }, //adjust according to content
						1500,
						'easeInOutExpo'
					),
					!1
				);
		}
	}),
		e('.scroll-animation').click(function () {
			e('.navbar-collapse').collapse('hide');
		}),
		e('body').scrollspy({ target: '#mainNav', offset: 54 });
	var t = function () {
		e('#mainNav').offset().top > 100
			? e('#mainNav').addClass('navbar-shrink')
			: e('#mainNav').removeClass('navbar-shrink');
	};
	t(), e(window).scroll(t);
})(jQuery);
